echoBarList

      bar1 = new Bar();
      bar1.setAge(33); 
      bar2 = new Bar();
      bar2.setAge(44);

      barList = new ArrayList();
      barList.add(bar1);
      barList.add(bar2);

      retList = echoImplPort.echoBarList(barList);
      assertEquals(2, retList.size());
      assertEquals(bar1.getAge(), retList.get(0).getAge());
      assertEquals(bar2.getAge(), retList.get(1).getAge());
